const paginate = (array, page_size, page_number) => {
    return array.slice((page_number - 1) * page_size, page_number * page_size);
}

let itemsOnThePage = 10;

msg.page = Number(msg.state.split("_")[2]) ? Number(msg.state.split("_")[2]) : 1

if (msg.textBtn == "Previous") {
    msg.page = Number(msg.state.split("_")[2]) - 1
    msg.items = paginate(msg.allItems, itemsOnThePage, msg.page)
} else if (msg.textBtn == "Next") {
    msg.page = Number(msg.state.split("_")[2]) + 1
    msg.items = paginate(msg.allItems, itemsOnThePage, msg.page)
} else {
    msg.items = paginate(msg.allItems, itemsOnThePage, msg.page)
}
msg.maxPage = Math.ceil(msg.allItems.length / itemsOnThePage);

