let trafficFineDetail = msg.payload["s:Envelope"]["s:Body"][0].FindTrafficFinesResponse[0].FindTrafficFinesResult[0]["a:FineDetails"][0]["b:TrafficFineDetail"] ?? [];
let fineDetails = msg.customerSettings.FineDetails ?? null;
if ((fineDetails == null || fineDetails == "" || fineDetails == "Pay Now")  && msg.previousState == 'querying_for_fines'){
    fineDetails = msg.text;
}
let TrafficFineDetailId = fineDetails.split(". ")[0] - 1;
msg.VehicleRegistration = fineDetails.split(". ")[1].split(" - ")[0];

let NoticeNumber = msg.customerSettings.TrafficFineDetail[TrafficFineDetailId].NoticeNumber ?? null;

for (let i = 0; i < trafficFineDetail.length; i++) {
    if (trafficFineDetail[i]["b:NoticeNumber"][0] == NoticeNumber) {
        msg.Amount = trafficFineDetail[i]["b:AmountDueRand"][0] ?? null;
        msg.NoticeNumber = trafficFineDetail[i]["b:NoticeNumber"][0] ?? null;
        msg.OffenceDate = trafficFineDetail[i]["b:OffenceDate"][0] ?? null;
        if (msg.OffenceDate != null){
            let date = new Date(msg.OffenceDate).toISOString().slice(0, 10);
            let time = new Date(msg.OffenceDate).toISOString().slice(11, 16);

            msg.OffenceDate = date+" "+time;
        }
        msg.IsExpressPaymentAllowed = trafficFineDetail[i]["b:IsExpressPaymentAllowed"][0] ?? null;
        msg.ExpressLink = trafficFineDetail[i]["b:ExpressLink"][0] ?? null;
        msg.PaymentAllowedReason = trafficFineDetail[i]["b:PaymentAllowedReason"][0] ?? null;
        msg.trafficFineDetail = trafficFineDetail[i];
        break;
    }
}
return msg;

